import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Main({ title, description}) {
  const [checkNovelty, setCheckNovelty] = useState(false);
  const [patentData, setPatentData] = useState("");

  // Fetch patent data from the backend when the component mounts.
  useEffect(() => {
    const getPatentData = async () => {
      try {
        const response = await axios.post("http://localhost:5000/generate", {
            title: title,
            description: description
        });
        if (response.status === 200) {
          setPatentData(response.data);
        }
      } catch (error) {
        console.error("Error fetching patent data:", error);
      }
    };
    getPatentData();
  }, [title, description]);

  // Function to check novelty using the /search endpoint.
  const novelty = async () => {
    try {
      const response = await axios.post("http://localhost:5000/search", {
        query: description // adjust this as needed
      });
      if (response.status === 200) {
        // response.data should include { isNovel: true/false }
        setCheckNovelty(response.data.isNovel);
      }
    } catch (error) {
      alert("Error during novelty check: " + error.message);
    }
  };

  return (
    <div>
      {checkNovelty ? (
        <h2>Your patent is novel</h2>
      ) : (
        <h2>There are too many similarities to your patent</h2>
      )}
      {/* Display the fetched patent data */}
      <p>{patentData}</p>
      {/* Optionally, add a button to trigger novelty check */}
      <button onClick={novelty}>Check Novelty</button>
    </div>
  );
}
