import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Input.css";

export default function Input({ title, setTitle, description, setDescription }) {
  const navigate = useNavigate();

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/submit", {
        title,
        description,
      });
      if (response.status === 200) {
        navigate("/patent", { state: { title, description } });
      }
    } catch (error) {
      console.error("Error submitting patent:", error);
      alert("Error submitting patent: " + error.message);
    }
  };

  return (
    <div className="landing-container">
      {/* Hero / Landing Section */}
      <div className="hero-section">
        <h1 className="hero-title">Bring Your Ideas to Life</h1>
        <p className="hero-subtitle">
          Transform your concepts into robust, legally sound patents with ease.
        </p>
      </div>

      {/* Form Container (Card) */}
      <div className="form-card">
        <h2 className="form-heading">Get Started</h2>
        <p className="form-subtext">
          Enter your invention details below and let our platform do the rest.
        </p>
        <form className="input-form" onSubmit={handleSubmit}>
          <label htmlFor="title" className="input-label">
            Patent Title
          </label>
          <input
            id="title"
            className="input-field"
            type="text"
            required
            value={title}
            onChange={handleTitleChange}
            placeholder="Enter a concise title"
          />

          <label htmlFor="description" className="input-label">
            Patent Description
          </label>
          <textarea
            id="description"
            className="input-field textarea-field"
            rows="4"
            required
            value={description}
            onChange={handleDescriptionChange}
            placeholder="Describe your invention in detail..."
          />

          <button type="submit" className="submit-button">
            Generate Patent
          </button>
        </form>
      </div>
    </div>
  );
}
